import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { useParams } from "react-router-dom"
import axios from 'axios'



function GenreInstance(props) {
    const { genreId } = useParams()
    console.log(genreId)

    const [genreData, setGenreData] = useState({
        "genreId": genreId,
        "name": "GenreName",
        "popularArtistsId": ["73sIBHcqh3Z3NyqHKZ7FOL", "Artists 2", "Artists 3"],
        "upcomingEventsId": ["Z698xZu0ZaGQo", "Event 2", "Event 3", "Event 4"],
        "topSongs": ["Song 1", "Song 2", "Song 3"],
        "eventsPriceRange": [0, -1]
    })

    useEffect(() => {
        const getGenreData = async () => {
            try {
                const response = axios.get(`/api/GetGenre/${genreId}`)
                setGenreData(response.data)
            } catch (error){
                console.error('Error:', error);
            }
        }
        getGenreData();
    },[genreId])

    useEffect(() => {
        const fetchEventNames = async () => {
            const eventNames = [];
            const limitedFutureEvents = props.upcomingEvents.slice(0, 3);

            while (limitedFutureEvents.length < 3) {
                limitedFutureEvents.push("");
            }

            for (let i = 0; i < 3; i++) {
                if (limitedFutureEvents[i] !== "") {
                    try {
                        const response = await axios.post(`/api/GetEvent/${limitedFutureEvents[i]}`);
                        eventNames[i] = response.data.eventName;
                    } catch (error) {
                        console.error('Error:', error);
                    }
                }
            }
            const eventIdPairs = Object.fromEntries(
                limitedFutureEvents.map((key, index) => [key, eventNames[index]])
            )
            setEventIdPairs(eventIdPairs);
        }
        fetchEventNames();
    }, [props.upcomingEvents]);

    return (
        <>
            <div class="d-flex flex-column align-items-center genre-page  text-start mt-5">
                <div>
                    <h1 class="genre-page-title mb-5 ">{genreData["name"]}</h1>
                </div>

                <div class="genre-page-con d-flex flex-column mb-5">
                    <h1 class="genre-page-subtitle">Top songs </h1>
                    {
                        genreData["topSongs"].map((song, index) => (
                            index < 3 ?
                            <h1 key ={index} class="genre-page-text">{song}</h1>
                            : 
                            null
                        ))
                    }

                </div>
                <div class="genre-page-con d-flex flex-column mb-5">
                    <h1 class="genre-page-subtitle">Top Artists </h1>
                    {
                        genreData["popularArtistsId"].map((artist, index) => (
                            index < 3 ?
                            <h1 key ={index} class="genre-page-text "><Link class=" genre-page-link" to={'/artists/artistspage/${artist}'}>{artist}</Link></h1>
                            : 
                            null
                        ))
                    }
                    {/* <h1 class="genre-page-text "><Link class=" genre-page-link" to="">FirstName</Link></h1>*/}
                </div>
                <div class="genre-page-con genre-page-venue d-flex flex-column mb-5 p-2 pb-3 rounded-4">
                    <h1 class="genre-page-subtitle mt-2">Venues </h1>
                    <h1 class="genre-page-price mb-4 ">${genreData["eventsPriceRange"][0]} - ${genreData["eventsPriceRange"][1]}</h1>
                    {
                        genreData["upcomingEventsId"].map((event, index) => (
                            index < 3 ?
                            <h1 key ={index} class="genre-page-text mb-2"><Link class=" genre-page-link" to="">{event}</Link></h1>
                            : 
                            null
                        ))
                    }


                </div>
            </div>

        </>
    )
}

export default GenreInstance;
