import React from "react";
import { useParams } from "react-router-dom";
import { useState } from 'react';
import axios from 'axios'

// useState has 

function ArtistsPage(props) {
    const { artistId } = useParams();
    const [artistData, setArtistData] = useState({
        "name": "Childish Gambino",
        "id": "73sIBHcqh3Z3NyqHKZ7FOL",
        "popularity": 79,
        "albums": ["Album 1", "Album 2", "Album 3"],
        "album_covers": ["https://i.scdn.co/image/ab67616d0000b273a9afb2484111b87abc2b4a80", "https://i.scdn.co/image/ab67616d0000b2737582716b3666a5235d5af4ea", "https://i.scdn.co/image/ab67616d0000b273d98a6832141788d8c355852b"],
        "future_events": ["Z698xZu0ZaGQo", "Z698xZbpZ17GA_K", "Event 3 id", "Event 4 id"],
        "image_url": "https://i.scdn.co/image/ab6761610000f178c3dc5429b676b16d451e5f77",
        "genre_id": "genreID",
    })

    // /api/GetArtist/<string:artist_id>

    axios.post(`/api/GetArtist/${artistId}`).then(response => {
        setArtistData(response)
    })
        .catch(error => {
            console.error('Error:', error);
        });

    const dictionary = Object.fromEntries(
        artistData.albums.map((key, index) => [key, artistData.album_covers[index]])
    );



    return (
        <>
            <div class="artist-page m-5 g-5 text-start d-flex flex-column">
                <h1 class="artist-page-title">{props.name}</h1>
                <h3 class="artist-page-text">#{props.popularity}</h3>
                <h1 class="artist-page-text mb-5"><a class="">{props.genre_id}</a></h1>
                <h1 class="artist-page-subtitle" >Albums</h1>
                <div class="row artist-page-albums flex-wrap">
                    {
                        artistData.albums.map((album) => (
                            <div class="text-black col-lg-4">
                                <img class="artist-page-album" src="https://i.scdn.co/image/ab67616d0000b2736cfd8376a0faaf6e9a5240b6" alt="albumCover" />
                                <p><b>{album}</b></p>
                            </div>
                        ))
                    }
                </div>
                <h1 class="artist-page-subtitle mb-4">Venues</h1>
                <div class=" d-flex flex-column ">
                    {
                        artistData.futureEvents.map((venue) => (
                            <p><b><a class="artist-page-smalltext  ">{venue}</a></b></p>
                        ))
                    }
                </div>
            </div >


        </>
    );
}

export default ArtistsPage;

