import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [homeData, setHomeData] = useState({
    title: "Cheap Cheap Tickets",
    subtitle: "Cheap tickets, Unforgettable Memories!",
    description: "Start with one of the buttons below and begin exploring for the perfect events for you based on what is most important!",
    buttons: [
      { to: "/artists", text: "Artists" },
      { to: "/genre", text: "Genres" },
      { to: "/venue", text: "Events" },
    ]
  });

  const fetchData = useCallback(async () => {
    try {
      // Fetch home data if needed
      // const response = await axios.get('/GetHomeData');
      // setHomeData(response.data);
    } catch (error) {
      console.error("Error:", error);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return (
    <div className="home-container d-flex justify-content-center align-items-center flex-column">
      <h2 className="home-page-text">{homeData.title}</h2>
      <p className="home-page-text">{homeData.subtitle}</p>
      <hr />
      <p className="home-page-text">{homeData.description}</p>
      <div className="d-grid gap-4 col-6 mx-auto">
        {homeData.buttons.map((button, index) => (
          <Link key={index} to={button.to} className="btn btn-primary">
            {button.text}
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Home;
