import React from "react";
import { Link } from 'react-router-dom';
import axios from 'axios'

function ArtistsCard(props) {

    function getGenreName(genre_id) {
        axios.post(`/api/GetGenre/${genre_id}`).then(response => {
            return response
        }).catch(error => console.error("Error", error))
    }

    const eventNames = ["defaultEventName 1", "defaultEventName 2"]
    props.futureEvents.forEach((event, i) => {
        axios.post('/api/GetEvent/', { event }).then(response => {
            eventNames[i] = response["eventName"];
        }).catch(error => {
            console.error('Error:', error)
        })
    });
    const eventIdPairs = Object.fromEntries(
        eventNames.map((key, index) => [key, props.future_events[index]])
    );


    return (
        <div class="card  artist-card p-2 border-0">

            <div class="d-flex justify-content-center mt-3">
                <img class="card-img-top circle-image" src={props.image_url} alt="artistsPic" />
            </div>

            <div class="card-body text-start d-flex flex-column">
                <div class="artist-card-container mb-4">
                    {
                        props.name.length < 6 ?
                            <Link class=" artist-card-link artist-card-title" to={'/artists/artistspage/${props.id}'}>{props.name}</Link>
                            :
                            props.name.length < 11 ?
                                <Link class=" artist-card-link artist-card-title2" to={'/artists/artistspage/${props.id}'}>{props.name}</Link>
                                :
                                <Link class=" artist-card-link artist-card-title3" to={'/artists/artistspage/${props.id}'}>{props.name}</Link>
                    }
                </div>

                <span><h1 class="artist-card-text">#{props.popularity}</h1></span>
                <span><h1 class="artist-card-text"><Link class="artist-card-link artist-card-genre" to={'/genre/${props.genreId}'}>{props.genreId}</Link></h1></span>
                <span><h1 class="artist-card-text">Latest Album : {props.albums[0]}</h1></span>
            </div>

            <div class="card-body text-start d-flex flex-column">
                <h1 class="artist-card-subtitle">Venues</h1>
                {
                    Object.entries(eventIdPairs).map(([key, value]) => (
                        props.futureEvents[0].length > 17 ?
                            <span><Link class="artist-card-link artist-card-text" to={`/venue/${value}`}>{key}</Link></span>
                            :
                            <span><Link class="artist-card-link artist-card-text" to={`/venue/${value}`}>{key.substring(0, 17) + "..."}</Link></span>
                    ))
                }
            </div>

        </div>
    );
}

export default ArtistsCard;
