import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios'


function VenueCard(props) {

    const [genreName, setGenreName] = useState("")
    useEffect(() => {
        const getGenreName = async () => {
            try {
                const response = await axios.get(`/api/GetGenre/${props.genreId}`);
                setGenreName(response.data.name)
            } catch (error) {
                console.error("Error:", error)
            }
        }
        getGenreName()
    },[props.genreId]);

    return (
        <div className="card mb-3">
            <div className="row g-0">
                <div className="col-md-4">
                    <img
                        src={imageUrl} // Ensure this prop is passed
                        className="img-fluid rounded-start"
                        alt={eventName}
                        style={{
                            width: '100%',
                            height: '100%',
                            objectFit: 'cover',
                            objectPosition: 'center'
                        }}
                    />
                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <Link to={`/venue/${eventId}`}>
                            <h5 className="card-title">{eventName}</h5>
                        </Link>
                        <p>Ticket Price: <strong>${priceRange[0]} to ${priceRange[1]}</strong></p>
                        <p className="card-text">{`${dataAndTime[0]}-${dataAndTime[1]}-${dataAndTime[2]}`}</p>
                        <Link to="#" className="btn btn-primary">
                            {artistNames[0]}
                        </Link>
                        <p className="card-text">
                            <small className="text-body-secondary">{venue.address}</small>
                        </p>
                        <p>
                            <strong>Genres: </strong>
                            <Link to={`/genre/${genreId}`}>{genreName}</Link>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default VenueCard;
