import React from "react";
import { useParams } from "react-router-dom";


function ArtistsPage(props) {
    const { artistId } = useParams();
    // const artist = {
    //     name: "Austin",
    //     popularity: 12,
    //     genreId: "genre ID",
    //     albums: ["this", "dsf", "dsf", "tha", "thifs", "dsf", "tha"],
    //     "album_covers": [
    //         "https://i.scdn.co/image/ab67616d0000b273a9afb2484111b87abc2b4a80",
    //         "https://i.scdn.co/image/ab67616d0000b2737582716b3666a5235d5af4ea",
    //         "https://i.scdn.co/image/ab67616d0000b273d98a6832141788d8c355852b"
    //     ],

    //     futureEvents: ["Hy-Vee INDYCAR Sunday Race + Post Malone & Kelsea Ballerini", "The 2nd Annual Capital City Blues Festival", "Broccoli City Festival 2-day Ticket (7/27-7/28)"]
    // }

    const artist = {
        "name": "artists name",
        "popularity": -1,
        "genreId": "genreID",
        "albums": ["Album 1", "Album 2", "Album 3"],
        "album_covers": ["https://i.scdn.co/image/ab67616d0000b273d98a6832141788d8c355852b", "url", "url 2"],
        "futureEvents": ["Event 1 id", "Event 2 id", "Event 3 id", "Event 4 id"],
        "image_url": "https://i.scdn.co/image/ab6761610000f178c3dc5429b676b16d451e5f77"
    }

    // create a dictionary with
    // dictionary = {key: value for key, value in zip(keys, values)}


    return (
        <>
            <div class="artist-page m-5 g-5 text-start d-flex flex-column">
                <h1 class="artist-page-title">{props.name}</h1>
                <h3 class="artist-page-text">#{props.popularity}</h3>
                <h1 class="artist-page-text mb-5"><a class="">{props.genreId}</a></h1>
                <h1 class="artist-page-subtitle" >Albums</h1>
                <div class="row artist-page-albums flex-wrap">
                    {
                        artist.albums.map((album) => (
                            <div class="text-black col-lg-4">
                                <img class="artist-page-album" src="https://i.scdn.co/image/ab67616d0000b2736cfd8376a0faaf6e9a5240b6" alt="albumCover" />
                                <p><b>{album}</b></p>
                            </div>
                        ))
                    }
                </div>
                <h1 class="artist-page-subtitle mb-4">Venues</h1>
                <div class=" d-flex flex-column ">
                    {
                        artist.futureEvents.map((venue) => (
                            <p><b><a class="artist-page-smalltext  ">{venue}</a></b></p>
                        ))
                    }
                </div>
            </div >


        </>
    );
}

export default ArtistsPage;

