import React from 'react';
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import axios from 'axios'



function GenreCard(props) {

    const [eventIdPairs, setEventIdPairs] = useState({});
    useEffect(() => {
        const fetchEventNames = async () => {
            const eventNames = ["defaultEventName 1", "defaultEventName 2", "defaultEventName 3"];
            const limitedFutureEvents = props.upcomingEvents.slice(0, 3);

            while (limitedFutureEvents.length < 3) {
                limitedFutureEvents.push("");
            }
            for (let i = 0; i < 3; i++) {
                if (limitedFutureEvents[i] !== "") {
                    try {
                        const response = await axios.post(`/api/GetEvent/${limitedFutureEvents[i]}`);
                        eventNames[i] = response.data.eventName;
                    } catch (error) {
                        console.error('Error:', error);
                    }
                }
            }
            const eventIdPairs = Object.fromEntries(
                limitedFutureEvents.map((key, index) => [key, eventNames[index]])
            )
            setEventIdPairs(eventIdPairs);
        }
        fetchEventNames();
    }, [props.futureEvents]);

    const [popularArtists, setpopularArtists] = useState({});
    useEffect(() => {
        const fetchArtistNames = async () => {
            const artistsNames = ["art 1", "art 2", "art 3"];
            const limitedFutureEvents = props.futureEvents.slice(0, 3);

            while (limitedFutureEvents.length < 3) {
                limitedFutureEvents.push("");
            }
            for (let i = 0; i < 3; i++) {
                if (limitedFutureEvents[i] !== "") {
                    try {
                        const response = await axios.post(`/api/GetEvent/${limitedFutureEvents[i]}`);
                        eventNames[i] = response.data.eventName;
                    } catch (error) {
                        console.error('Error:', error);
                    }
                }
            }

            const eventIdPairs = Object.fromEntries(
                limitedFutureEvents.map((key, index) => [key, eventNames[index]])
            );
            setEventIdPairs(eventIdPairs);
        };
        fetchEventNames();
    }, [props.futureEvents]);




    return (
        <>
            <div class="genre-card row text-start p-5 rounded-5">

                <h1 class="col-lg-12  "><Link class="genre-card-title" to={'/genre/${props.genreId}'}>{props.name}</Link></h1>
                <div class="col-lg-4 d-flex flex-column">
                    <h1 class="genre-card-subtitle">Top Songs</h1>
                    {
                        props.topSongs.map((song, index) => (
                            index < 3 ?
                                <span key={index}>{song}</span>
                                :
                                null
                        ))
                    }
                </div>

                <div class="col-lg-4 d-flex flex-column">
                    <h1 class="genre-card-subtitle" >Popular Artists</h1>
                    {
                        props.popularArtists.map((artist, index) => (
                            index < 3 ?
                                <span key={index}><Link to={'/artists/artistspage/${artist}'} class="genre-card-link">{artist}</Link></span>
                                :
                                null
                        ))
                    }
                    {/* <span><Link to={"/genre/genrestaticinstance1"} class="genre-card-link">First Last Name</Link></span> */}
                </div>

                <div class="col-lg-4 d-flex flex-column">
                    <h1 class="genre-card-subtitle">Venues</h1>
                    {
                        props.upcomingEvents.map((event, index) => (
                            index < 3 ?
                                <span key={index}><Link to={"/genre/$(event)"} class="genre-card-link">{event}</Link></span>
                                :
                                null

                        ))
                    }
                    {/* <span><Link to={"/genre/genrestaticinstance1"} class="genre-card-link">example event</Link></span> */}
                </div >


            </div >
        </>

    );
}

export default GenreCard;
