import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';  // Assume you have a CSS file for custom styles

function Home() {
  const [homeData, setHomeData] = useState({
    title: "Cheap Cheap Tickets",
    subtitle: "Cheap tickets, Unforgettable Memories!",
    description: "Start with one of the buttons below and begin exploring for the perfect events for you based on what is most important!",
    buttons: [
      { to: "/artists", text: "Artists" },
      { to: "/genre", text: "Genres" },
      { to: "/venue", text: "Events" },
    ],
    backgroundImage: "/path/to/your/background-image.jpg",  // Update with actual image path
    promoVideo: "/path/to/your/promo-video.mp4"  // Update with actual video path
  });

  const fetchData = useCallback(async () => {
    try {
      // Fetch home data if needed
      // const response = await axios.get('/GetHomeData');
      // setHomeData(response.data);
    } catch (error) {
      console.error("Error:", error);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return (
    <div className="home-container d-flex justify-content-center align-items-center flex-column text-center" style={{ backgroundImage: `url(${homeData.backgroundImage})` }}>
      <div className="overlay"></div> {/* Optional overlay for better text readability */}
      <h2 className="home-page-text display-4 text-white">{homeData.title}</h2>
      <p className="home-page-text lead text-white">{homeData.subtitle}</p>
      <hr className="home-divider" />
      <p className="home-page-text text-white mb-4">{homeData.description}</p>
      <div className="d-grid gap-4 col-6 mx-auto">
        {homeData.buttons.map((button, index) => (
          <Link key={index} to={button.to} className="btn btn-lg btn-outline-light shadow-sm">
            {button.text}
          </Link>
        ))}
      </div>
      <div className="promo-video-container mt-5">
        <video className="promo-video" controls>
          <source src={homeData.promoVideo} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
    </div>
  );
}

export default Home;
